const angular = require('angular');

function sportsStoreCtrl($scope) {
    const product = (name, description, category, price) => ({ name, description, category, price });

    $scope.data = {
        products: [
            product("Product #1", "A product", "Category #1", 100),
            product("Product #2", "A product", "Category #1", 110),
            product("Product #3", "A product", "Category #2", 210),
            product("Product #4", "A product", "Category #3", 202)
        ]
    }
}

function productListCtrl($scope, $filter, productListActiveClass, productListPageCount) {
    let selectedCategory = null;

    Object.assign($scope, {
        selectedPage: 1,
        pageSize: productListPageCount,

        selectPage(newPage) {
            $scope.selectedPage = newPage;
        },
        selectCategory(cat) {
            selectedCategory = cat;
            $scope.selectedPage = 1;
        },
        categoryFilterFn(product) {
            return !selectedCategory || product.category === selectedCategory;
        },
        getCategoryClass(cat) {
            return selectedCategory === cat ? productListActiveClass : '';
        },
        getPageClass(page) {
            return page === $scope.selectedCategory ? productListActiveClass : '';
        }
    });
}

function uniqueFilter() {
    return function (data, propertyName) {
        if (Array.isArray(data) && angular.isString(propertyName)) {
            const set = new Set();
            return data
                .filter(object => {
                    const val = object[propertyName];
                    if (set.has(val))
                        return false;

                    set.add(val);
                    return true;
                });
        } else {
            return data;
        }
    }
}

function rangeFilter($filter) {
    return function (data, page, size) {
        if (angular.isArray(data) && angular.isNumber(page) && angular.isNumber(size)) {
            const start_index = (page - 1) * size;
            if (data.length < start_index)
                return [];
            else
                return $filter("limitTo")(data.splice(start_index), size);
        } else {
            return data;
        }
    }
}

function pageCountFilter() {
    return function (data, size) {
        if (angular.isArray(data) && angular.isNumber(size)) {
            const len = Math.ceil(data.length / size);
            const result = [];
            for (let i = 0; i < len; i++)
                result.push(i);

            return result;
        } else {
            return data;
        }
    }
}

// register filters

const customFilters = angular.module('customFilters', []);

[
    uniqueFilter,
    rangeFilter,
    pageCountFilter
].forEach(f => customFilters.filter(f.name.replace(/Filter$/, ''), f));


const sportsStore = angular.module('sportsStore', ["customFilters"]);

sportsStore
    .constant('productListActiveClass', 'btn-danger')
    .constant('productListPageCount', 3)
    ;


// register controllers
[
    sportsStoreCtrl,
    productListCtrl
].forEach(f => sportsStore.controller(f.name, f));
